public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=25, y=5, z=3;
		
		
		System.out.println(2^2);
		//^ does not work as an exponent in Java
		
		System.out.println(Math.sqrt(64));
		
		System.out.println(Math.sqrt(x));

		System.out.println(Math.pow(9,2));
		
		System.out.println(Math.pow(y,z));

		System.out.println(Math.abs(-3.4));

		System.out.println(Math.round(-5.1));
		
		System.out.println(Math.round(7.9));
		
		System.out.println(Math.ceil(5.1));
		
		System.out.println(Math.floor(5.1));
		
		System.out.println(Math.floor(5.9));
		
		System.out.println(Math.sqrt(Math.abs(-9)));
		
		System.out.println(Math.abs(Math.sqrt(Math.pow(3, 2))));
		
		System.out.println(Math.pow(z,2)*Math.PI);
		
	}

}